import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Card, Button, CardTitle, CardText, Row, Col, Container } from 'reactstrap';
import Form from 'react-bootstrap/Form';

const SurveyBirthYear = () => {

  const [year, setYear] = useState(0)

  const checkBirthYear = (e)=>{
      setYear(e.currentTarget.value)
  }

  const moveNextPage = ()=>{

      console.log(year);

      axios.post(
          "/post", {
              year : year
          }
      ).then(res => {
          console.log(res.data);
      }).catch(()=>{
          console.log('error');
      })

      window.location.replace("/survey/item")
  }
  const [yearList, setYearList] = useState([])

  let list = []

  useEffect(()=>{
      addList();
      setYearList(list)
  },[])

  const addList = ()=> {
      for(let i = 1930 ; i <= 2013 ; i++){
          list.push(i)
      }
  }

  return (
    <div className="wrapper">
            <div className="middle-spacer survey-year">
                <Container>
                    <Row className="justify-content-center">
                        <Col md="7" className="text-center">
                            <h1 className="title font-bold">태어난 연도를 알려주세요.</h1>
                        </Col>
                    </Row>
                </Container>
                <Form.Select aria-label="Default select example" name="selectAge" className="justify-content-center select-box" onChange={checkBirthYear}>
                {
                yearList.map((item)=>(
                    <option value={item}>{item}년</option>
                ))}
            </Form.Select>
            <Button color="success" size='lg' onClick={moveNextPage}>다음</Button>
            </div>
        </div>
  )
}

export default SurveyBirthYear
