import React, { useEffect } from 'react'
import { Container, Progress, Row, Col } from 'reactstrap'
import axios from 'axios'
import { useState } from 'react'

const RecAmountNutri = ({item}) => {

  const [list, setList] = useState([])
  const [min, setMin] = useState()
  const [max, setMax] = useState()

  useEffect(()=>{
    axios.get('http://localhost:3000/haru/preview/nutri.json')
    .then((res)=>{
      console.log('통신성공');
      console.log('res', res.data);
      setList(res.data)
    })
  }, [])

//  const getMinMaxByName = (name) => {
//     const object = list.find(obj => obj.name === name);
//     if (object) {
//       return [object.intakeMin, object.intakeMax];
//     }
//     return [null, null];
//   };

//   useEffect(()=>{
//     // getMinMaxByName(`${item.nutri_name}`)
//     let name1= item.nutri_name
    
//     const [min, max] = getMinMaxByName(name1)

//   },[list])

useEffect(() => {
  if (list.length > 0) {
    const [minValue, maxValue] = getMinMaxByName(item.nutri_name);
    setMin(minValue);
    setMax(maxValue);
  }
}, [list, item.nutri_name]);

const getMinMaxByName = (name) => {
  const object = list.find((obj) => obj.name === name);
  if (object) {
    return [object.intakeMin, object.intakeMax];
  }
  return [null, null];
};

  return (
    <div>
      <Container>
        <Row>
          <Col>{item.nutri_name}</Col>
          <Col>
          <Container>
            <Row><Col>성인기준 일 권장량 대비 80%</Col></Row>
            <Row>
              <Col>
                <Progress
                  value={item.nutri_content}
                  min={10}
                  max={20}
                >{item.nutri_content}</Progress>
              </Col>          
            </Row>
            <Row>
              <Col>10mg</Col>
              <Col>20mg</Col>
            </Row>
          </Container>
          </Col>
        </Row>
      </Container>


    </div>
  )
}

export default RecAmountNutri
