import React, { useState } from 'react'
import PriceTotalChart from './PriceTotalChart';
import RecAmountNutri from './RecAmountNutri';
import { useEffect } from 'react';

const PriceTotal = ({result}) => {

  const [ totalPrice, setTotalPrice ] = useState()
  const [ totalPriceOnday, setTotalPriceOneday] = useState()
  let name1 = ""
  let value1 = 0
  let obj = {}
  let data = []
  
  useEffect(()=>{
    console.log('price total 콤포넌트 result', result);
    let temp = 0;
    let tempOne = 0;

    result.map((item)=>(
      temp += item.detail_price))

    result.map((item)=>(
      tempOne += item.day_price
    ))

    result.map((item)=>(
      name1 = item.detail_name,
      value1 = item.detail_price,
      obj = {name : {name1}, value : {value1}},
      data += obj
    ))
    console.log('obj', obj);
    console.log('temp', temp);
    setTotalPrice(temp)
    setTotalPriceOneday(tempOne)
  },[result])


  return (
    <div>
      <div>
        <PriceTotalChart/>
      </div>
      {/* 텍스트 */}
      <div>
        <h5>{result.length}가지 영양제 구매시 총 금액</h5>
        <p>{totalPrice}원</p>
        <p>하루에 {totalPriceOnday}원</p>
      </div>
    </div> 
  )
}

export default PriceTotal
