import React, { useState } from 'react'
import axios from 'axios'
import { Alert, Container, Row, Col } from 'reactstrap';

const NutriFaq = ({nutri_name}) => {

    const [questionList, setQuestionList] = useState([])
    const [answerList, setAnswerList] = useState([])

    useState(()=>{
        console.log('선택한 영양성분 :',  nutri_name);
        axios.get(`http://localhost:8085/haru/nutrifaq/${nutri_name}`)
        .then((res)=>{
            console.log(res.data);
            console.log('nutrifaq 통신성공',res.data[0].nutriFaq.question);
            let questionTemp = []
            for(let i = 0 ; i <= res.data.length ; i++){
                questionTemp.push(res.data[i].nutriFaq.question)
            }

            console.log(questionTemp);
            
        })
        .catch((e)=>{
            console.log('error : ', e);
        })
    },[])

  return (
    <div>
        <Container>
                <Row>
                    <Col md="12">
                        <Alert color="info">
                            {questionList.map((item)=>(
                                <h6>{item}</h6>
                            ))}
                            {answerList.map((item)=>(
                                <p>{item}</p>
                            ))}
                            
                            </Alert>
                    </Col>
                </Row>
            </Container>

    </div>
  )
}

export default NutriFaq
